subroutine iotk_write_dat_REAL1_1(unit,name,dat,fmt,ierr)
  use iotk_base
  use iotk_interface
  implicit none
  integer,                intent(in)  :: unit
  character(*),           intent(in)  :: name
  REAL (kind=__IOTK_REAL1), intent(in)  :: dat (:) 
  character(*), optional, intent(in)  :: fmt
  integer,      optional, intent(out) :: ierr
  integer :: ierrl,lunit,iostat
  logical :: binary,raw
  integer(iotk_header_kind), parameter :: idummy=0
  character(300) :: usefmt,usefmt1
  character(iotk_attlenx) :: attr
  ierrl = 0
  iostat = 0
  lunit = iotk_physical_unit(unit)
  call iotk_unit_get(lunit,raw=raw,ierr=ierrl)
  call iotk_error_clear(ierrl)
  call iotk_inquire(lunit,binary=binary,ierr=ierrl)
  if(ierrl/=0) then
        call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
    goto 1
  end if
  usefmt = "!"
  if(present(fmt) .and. .not. raw) usefmt = fmt
! Per ora controllo solo che i caratteri di cui fare l'escape
! non compaiano nelle stringhe di formato e nel dato se e' un carattere.
  if(scan(usefmt,"<>&")/=0) then
        call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
    call iotk_error_msg(ierrl,'Special characters (<>&) found in fmt string')
    call iotk_error_write(ierrl,"unit",unit)
    call iotk_error_write(ierrl,"name",trim(name))
    call iotk_error_write(ierrl,"fmt",trim(fmt))
    goto 1
  end if
  call iotk_write_attr(attr,"type",iotk_tolower("REAL"),first=.true.,ierr=ierrl)
  if(ierrl/=0) then
        call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
    goto 1
  end if
  call iotk_write_attr(attr,"size",iotk_size(dat),ierr=ierrl)
  if(ierrl/=0) then
        call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
    goto 1
  end if
  if(binary) then
    call iotk_write_attr(attr,"kind",kind(dat),ierr=ierrl)
    if(ierrl/=0) then
          call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
      goto 1
    end if
  end if
  if(usefmt/="!") call iotk_write_attr(attr,"fmt",trim(usefmt),ierr=ierrl)
  if(ierrl/=0) then
        call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
    goto 1
  end if
  call iotk_write_begin(unit,name,attr,ierrl)
  if(ierrl/=0) then
        call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
    goto 1
  end if
  if(binary) then
    if(raw) then
      write(lunit,iostat=iostat) dat
      if(iostat/=0) then
            call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
        goto 1
      end if
    else
      write(lunit,iostat=iostat) idummy,dat
      if(iostat/=0) then
            call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
        goto 1
      end if
    end if
  else
    usefmt = iotk_wfmt("REAL",kind(dat),1,-1,usefmt)
    if(usefmt=="*") then
      write(lunit,*,iostat=iostat) dat
      if(iostat/=0) then
            call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
        goto 1
      end if
    else
      write(lunit,fmt=usefmt,iostat=iostat) dat
      if(iostat/=0) then
            call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
        goto 1
      end if
    end if
  end if
  call iotk_write_end(unit,name,ierr=ierrl)
  if(ierrl/=0) then
        call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
    goto 1
  end if
1 continue
  if(present(ierr)) then
    ierr = ierrl
  else
    if(ierrl/=0) call iotk_error_handler(ierrl)
  end if
end subroutine iotk_write_dat_REAL1_1

recursive subroutine iotk_scan_dat_aux_REAL1(unit,dat,rkind,rlen,fmt,ierr)
  use iotk_base
  use iotk_interface
  implicit none
  integer,         intent(in)  :: unit
  REAL (kind=__IOTK_REAL1), intent(out) :: dat (:)
  integer,         intent(in)  :: rkind
  integer,         intent(in)  :: rlen
  character(*),    intent(in)  :: fmt
  integer,         intent(out) :: ierr
  integer(iotk_header_kind) :: idummy
  logical :: raw,binary
  integer :: lunit
  integer :: index,length,nexttag,iostat
  character(len=iotk_linlenx) :: line
#ifdef __IOTK_REAL2
  REAL (__IOTK_REAL2), allocatable :: dat2 (:)
#endif
#ifdef __IOTK_REAL3
  REAL (__IOTK_REAL3), allocatable :: dat3 (:)
#endif
#ifdef __IOTK_REAL4
  REAL (__IOTK_REAL4), allocatable :: dat4 (:)
#endif
  lunit = iotk_physical_unit(unit)
  ierr = 0
  iostat = 0
  idummy = 0
  call iotk_unit_get(lunit,raw=raw,ierr=ierr)
  call iotk_error_clear(ierr)
  call iotk_inquire(unit=lunit,binary=binary,ierr=ierr)
  if(ierr/=0) then
        call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    return
  end if
  if(binary) then
    select case(rkind)
    case(kind(dat))
      if(raw) then
        read(lunit,iostat=iostat) dat
        if(iostat/=0) then
              call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
          return
        end if
      else
        read(lunit,iostat=iostat) idummy,dat
        if(iostat/=0) then
              call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
          return
        end if
      end if
#ifdef __IOTK_REAL2
    case(kind(dat2))
      ! Giusto per scrupolo. Se e' raw non ci sono info sul kind, quindi questa linea e' irraggiungibile
      if(raw) then
            call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
        return
      end if
      allocate(dat2(ubound(dat,1)))
      read(lunit,iostat=iostat) idummy,dat2
      if(iostat/=0) then
            call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
        return
      end if
      dat = dat2
      deallocate(dat2)
#endif
#ifdef __IOTK_REAL3
    case(kind(dat3))
      ! Giusto per scrupolo. Se e' raw non ci sono info sul kind, quindi questa linea e' irraggiungibile
      if(raw) then
            call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
        return
      end if
      allocate(dat3(ubound(dat,1)))
      read(lunit,iostat=iostat) idummy,dat3
      if(iostat/=0) then
            call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
        return
      end if
      dat = dat3
      deallocate(dat3)
#endif
#ifdef __IOTK_REAL4
    case(kind(dat4))
      ! Giusto per scrupolo. Se e' raw non ci sono info sul kind, quindi questa linea e' irraggiungibile
      if(raw) then
            call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
        return
      end if
      allocate(dat4(ubound(dat,1)))
      read(lunit,iostat=iostat) idummy,dat4
      if(iostat/=0) then
            call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
        return
      end if
      dat = dat4
      deallocate(dat4)
#endif
    case default
          call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,'Kind incompatibility')
    call iotk_error_write(ierr,"kind",rkind)
    end select
  else
    if(fmt=="*") then
      read(lunit,fmt=*,iostat=iostat) dat
      if(iostat/=0) then
            call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
        return
      end if
    else if(fmt=="!") then
      index = 0
      do
        call iotk_getline(lunit,line,length,ierr)
        if(ierr/=0) then
              call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
          return
        end if
        nexttag = scan(line(1:length),"<")
        if(nexttag==0) then
          nexttag = length + 1
        else
! AGGIUSTA LA POSIZIONE SE C'E' UNA TAG SU QUESTA LINEA
! E' UN PO' CASERECCIO MA FUNZIONA
          backspace(lunit,iostat=iostat)
          if(iostat/=0) then
                call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
            return
          end if
          read(lunit,"(a)",advance="no",iostat=iostat) line(1:nexttag-1)
          if(iostat/=0) then
                call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
            return
          end if
        end if
        call iotk_read(dat,line(1:nexttag - 1),index,ierr)
        if(ierr/=0) then
              call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
          return
        end if
        if(index == size(dat)) exit
        if(nexttag/=length + 1) then
              call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
          return
        end if
      end do
    else
      read(lunit,fmt=fmt,iostat=iostat) dat
      if(iostat/=0) then
            call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
        return
      end if
    end if
  end if
  if(idummy/=0) then
        call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    return
  end if
end subroutine iotk_scan_dat_aux_REAL1

subroutine iotk_scan_dat_REAL1_1(unit,name,dat,found,default,ierr)
  use iotk_base
