subroutine iotk_write_dat_REAL3_1(unit,name,dat,fmt,ierr)
  use iotk_base
  use iotk_interface
  implicit none
  integer,                intent(in)  :: unit
  character(*),           intent(in)  :: name
  REAL (kind=__IOTK_REAL3), intent(in)  :: dat (:) 
  character(*), optional, intent(in)  :: fmt
  integer,      optional, intent(out) :: ierr
  integer :: ierrl,lunit,iostat
  logical :: binary,raw
  integer(iotk_header_kind), parameter :: idummy=0
  character(300) :: usefmt,usefmt1
  character(iotk_attlenx) :: attr
  ierrl = 0
  iostat = 0
  lunit = iotk_physical_unit(unit)
  call iotk_unit_get(lunit,raw=raw,ierr=ierrl)
  call iotk_error_clear(ierrl)
  call iotk_inquire(lunit,binary=binary,ierr=ierrl)
  if(ierrl/=0) then
        call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
    goto 1
  end if
  usefmt = "!"
  if(present(fmt) .and. .not. raw) usefmt = fmt
! Per ora controllo solo che i caratteri di cui fare l'escape
! non compaiano nelle stringhe di formato e nel dato se e' un carattere.
  if(scan(usefmt,"<>&")/=0) then
        call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
    call iotk_error_msg(ierrl,'Special characters (<>&) found in fmt string')
    call iotk_error_write(ierrl,"unit",unit)
    call iotk_error_write(ierrl,"name",trim(name))
    call iotk_error_write(ierrl,"fmt",trim(fmt))
    goto 1
  end if
  call iotk_write_attr(attr,"type",iotk_tolower("REAL"),first=.true.,ierr=ierrl)
  if(ierrl/=0) then
        call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
    goto 1
  end if
  call iotk_write_attr(attr,"size",iotk_size(dat),ierr=ierrl)
  if(ierrl/=0) then
        call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
    goto 1
  end if
  if(binary) then
    call iotk_write_attr(attr,"kind",kind(dat),ierr=ierrl)
    if(ierrl/=0) then
          call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
      goto 1
    end if
  end if
  if(usefmt/="!") call iotk_write_attr(attr,"fmt",trim(usefmt),ierr=ierrl)
  if(ierrl/=0) then
        call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
    goto 1
  end if
  call iotk_write_begin(unit,name,attr,ierrl)
  if(ierrl/=0) then
        call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
    goto 1
  end if
  if(binary) then
    if(raw) then
      write(lunit,iostat=iostat) dat
      if(iostat/=0) then
            call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
        goto 1
      end if
    else
      write(lunit,iostat=iostat) idummy,dat
      if(iostat/=0) then
            call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
        goto 1
      end if
    end if
  else
    usefmt = iotk_wfmt("REAL",kind(dat),1,-1,usefmt)
    if(usefmt=="*") then
      write(lunit,*,iostat=iostat) dat
      if(iostat/=0) then
            call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
        goto 1
      end if
    else
      write(lunit,fmt=usefmt,iostat=iostat) dat
      if(iostat/=0) then
            call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
        goto 1
      end if
    end if
  end if
  call iotk_write_end(unit,name,ierr=ierrl)
  if(ierrl/=0) then
        call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
    goto 1
  end if
1 continue
  if(present(ierr)) then
    ierr = ierrl
  else
    if(ierrl/=0) call iotk_error_handler(ierrl)
  end if
end subroutine iotk_write_dat_REAL3_1

recursive subroutine iotk_scan_dat_aux_REAL3(unit,dat,rkind,rlen,fmt,ierr)
  use iotk_base
  use iotk_interface
  implicit none
  integer,         intent(in)  :: unit
  REAL (kind=__IOTK_REAL3), intent(out) :: dat (:)
  integer,         intent(in)  :: rkind
  integer,         intent(in)  :: rlen
  character(*),    intent(in)  :: fmt
  integer,         intent(out) :: ierr
  integer(iotk_header_kind) :: idummy
  logical :: raw,binary
  integer :: lunit
  integer :: index,length,nexttag,iostat
  character(len=iotk_linlenx) :: line
#ifdef __IOTK_REAL1
  REAL (__IOTK_REAL1), allocatable :: dat1 (:)
#endif
#ifdef __IOTK_REAL2
  REAL (__IOTK_REAL2), allocatable :: dat2 (:)
#endif
#ifdef __IOTK_REAL4
  REAL (__IOTK_REAL4), allocatable :: dat4 (:)
#endif
  lunit = iotk_physical_unit(unit)
  ierr = 0
  iostat = 0
  idummy = 0
  call iotk_unit_get(lunit,raw=raw,ierr=ierr)
  call iotk_error_clear(ierr)
  call iotk_inquire(unit=lunit,binary=binary,ierr=ierr)
  if(ierr/=0) then
        call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    return
  end if
  if(binary) then
    select case(rkind)
    case(kind(dat))
      if(raw) then
        read(lunit,iostat=iostat) dat
        if(iostat/=0) then
              call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
          return
        end if
      else
        read(lunit,iostat=iostat) idummy,dat
        if(iostat/=0) then
              call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
          return
        end if
      end if
#ifdef __IOTK_REAL1
    case(kind(dat1))
      ! Giusto per scrupolo. Se e' raw non ci sono info sul kind, quindi questa linea e' irraggiungibile
      if(raw) then
            call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
        return
      end if
      allocate(dat1(ubound(dat,1)))
      read(lunit,iostat=iostat) idummy,dat1
      if(iostat/=0) then
            call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
        return
      end if
      dat = dat1
      deallocate(dat1)
#endif
#ifdef __IOTK_REAL2
    case(kind(dat2))
      ! Giusto per scrupolo. Se e' raw non ci sono info sul kind, quindi questa linea e' irraggiungibile
      if(raw) then
            call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
        return
      end if
      allocate(dat2(ubound(dat,1)))
      read(lunit,iostat=iostat) idummy,dat2
      if(iostat/=0) then
            call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
        return
      end if
      dat = dat2
      deallocate(dat2)
#endif
#ifdef __IOTK_REAL4
    case(kind(dat4))
      ! Giusto per scrupolo. Se e' raw non ci sono info sul kind, quindi questa linea e' irraggiungibile
      if(raw) then
            call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
        return
      end if
      allocate(dat4(ubound(dat,1)))
      read(lunit,iostat=iostat) idummy,dat4
      if(iostat/=0) then
            call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
        return
      end if
      dat = dat4
      deallocate(dat4)
#endif
    case default
          call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,'Kind incompatibility')
    call iotk_error_write(ierr,"kind",rkind)
    end select
  else
    if(fmt=="*") then
      read(lunit,fmt=*,iostat=iostat) dat
      if(iostat/=0) then
            call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
        return
      end if
    else if(fmt=="!") then
      index = 0
      do
        call iotk_getline(lunit,line,length,ierr)
        if(ierr/=0) then
              call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
          return
        end if
        nexttag = scan(line(1:length),"<")
        if(nexttag==0) then
          nexttag = length + 1
        else
! AGGIUSTA LA POSIZIONE SE C'E' UNA TAG SU QUESTA LINEA
! E' UN PO' CASERECCIO MA FUNZIONA
          backspace(lunit,iostat=iostat)
          if(iostat/=0) then
                call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
            return
          end if
          read(lunit,"(a)",advance="no",iostat=iostat) line(1:nexttag-1)
          if(iostat/=0) then
                call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
            return
          end if
        end if
        call iotk_read(dat,line(1:nexttag - 1),index,ierr)
        if(ierr/=0) then
              call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
          return
        end if
        if(index == size(dat)) exit
        if(nexttag/=length + 1) then
              call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
          return
        end if
      end do
    else
      read(lunit,fmt=fmt,iostat=iostat) dat
      if(iostat/=0) then
            call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    call iotk_error_msg(ierr,' ')
    call iotk_error_write(ierr,"iostat",iostat)
        return
      end if
    end if
  end if
  if(idummy/=0) then
        call iotk_error_issue(ierr,"iotk_scan_dat_aux",__FILE__,__LINE__)
    return
  end if
end subroutine iotk_scan_dat_aux_REAL3

subroutine iotk_scan_dat_REAL3_1(unit,name,dat,found,default,ierr)
  use iotk_base
  use iotk_interface
  implicit none
  integer,                   intent(in)  :: unit
  character(*),              intent(in)  :: name
  REAL (kind=__IOTK_REAL3),           intent(out) :: dat (:)
  logical,         optional, intent(out) :: found
  REAL (kind=__IOTK_REAL3), optional, intent(in)  :: default (:)
  integer,         optional, intent(out) :: ierr
  REAL (kind=__IOTK_REAL3),              allocatable :: tmpdat(:)
  integer :: ierrl,ierrl2
  integer :: rkind,rsize,rlen
  character(iotk_vallenx) :: rtype
  character(iotk_vallenx) :: fmt
  character(iotk_attlenx) :: attr
  ierrl = 0
  ierrl2 = 0
  call iotk_scan_begin(unit,name,attr,ierr=ierrl)
  if(ierrl/=0) goto 1
  call iotk_parse_dat(attr,rtype,rkind,rsize,rlen,fmt,ierrl)
  if(ierrl/=0) then
    call iotk_scan_end(unit,name,ierr=ierrl2)
    goto 1
  end if
  if(ierrl/=0) goto 1
  if(iotk_strcomp(rtype,iotk_eos)) then
    rtype = "REAL"
  else
    if(.not. iotk_strcomp(rtype,"REAL")) then
          call iotk_error_issue(ierrl,"iotk_scan_dat",__FILE__,__LINE__)
      goto 1
    end if
  end if
  if(rkind==-1) rkind = kind(dat)
  if(rsize==-1) rsize = iotk_size(dat)
  if(rlen ==-1) rlen  = iotk_len(dat)
  if(rsize /= iotk_size(dat)) then
        call iotk_error_issue(ierrl,"iotk_scan_dat",__FILE__,__LINE__)
    goto 1
  end if

  allocate(tmpdat(iotk_size(dat)))
  call iotk_scan_dat_aux(unit,tmpdat,rkind,rlen,fmt(1:iotk_strlen(fmt)),ierrl)
        dat = reshape(tmpdat,shape(dat))
  deallocate(tmpdat)

  call iotk_scan_end(unit,name,ierr=ierrl2)
  if(ierrl2/=0) then
    call iotk_error_clear(ierrl)
    ierrl=ierrl2
  end if
1 continue
  if(present(found)) then
    found = .false.
    if(ierrl==0) found = .true.
  end if
  if(present(default)) then
    if(ierrl/=0) dat = default
  end if
  if(present(ierr)) then
    ierr = ierrl
  else
    if(ierrl>0 .or. (.not.present(found) .and. .not.present(default))) call iotk_error_handler(ierrl)
  end if
end subroutine iotk_scan_dat_REAL3_1



subroutine iotk_write_attr_REAL3_1(attr,name,val,first,ierr)
  use iotk_base
  use iotk_interface
  implicit none
  character(*), intent(inout) :: attr
  character(*), intent(in)    :: name
  REAL(kind=__IOTK_REAL3), intent(in)  :: val (:)
  logical, optional, intent(in)  :: first
  integer, optional, intent(out) :: ierr
  integer :: ierrl
  integer :: attlen
  integer :: vallen
  integer :: namlen
  integer :: iostat
  character :: delim
  character(iotk_vallenx) :: tmpval
  ierrl = 0
  iostat = 0
  if(present(first)) then
    if(first) attr(1:1) = iotk_eos
  end if
  if(.not.iotk_check_name(name)) then
        call iotk_error_issue(ierrl,"iotk_write_attr",__FILE__,__LINE__)
    call iotk_error_msg(ierrl,'Wrong tag name')
    call iotk_error_write(ierrl,"name",trim(name))
    goto 1
  end if
  attlen = iotk_strlen(attr)
  if(attlen==len(attr)) attlen = len_trim(attr)
  namlen = len_trim(name)
  delim = '"'
  call iotk_write(pack(val,mask=.true.),tmpval,ierrl)
  if(ierrl/=0) then
        call iotk_error_issue(ierrl,"iotk_write_attr",__FILE__,__LINE__)
    goto 1
  end if
  vallen = iotk_strlen(tmpval)
  if(attlen+vallen+namlen+5>len(attr)) then
        call iotk_error_issue(ierrl,"iotk_write_attr",__FILE__,__LINE__)
    call iotk_error_msg(ierrl,'Attribute dummy argument is too short')
    goto 1
  end if
  attr(attlen+1:attlen+vallen+namlen+5) = " "//trim(name)//"="//delim//tmpval(1:vallen)//delim//iotk_eos
1 continue
  if(present(ierr)) then
    ierr = ierrl
  else
    if(ierrl/=0) call iotk_error_handler(ierrl)
  end if
end subroutine iotk_write_attr_REAL3_1

subroutine iotk_scan_attr_REAL3_1(attr,name,val,found,default,eos,ierr)
  use iotk_base
  use iotk_interface
  implicit none
  character(*),             intent(in)  :: attr
  character(*),             intent(in)  :: name
  REAL(kind=__IOTK_REAL3),           intent(out) :: val (:)
  logical,        optional, intent(out) :: found
  REAL(kind=__IOTK_REAL3), optional, intent(in)  :: default (:)
  logical,        optional, intent(in)  :: eos
  integer,        optional, intent(out) :: ierr
  integer :: ierrl
  integer :: attlen,pos,equal
  character :: delim
  logical :: foundl
  character(iotk_vallenx) :: valc
  integer :: index
  REAL(kind=__IOTK_REAL3), allocatable :: tmpval (:)
  ierrl = 0
  attlen=iotk_strlen(attr)
  foundl = .false.
  equal = 0
  do
    pos = verify(attr(equal+1:attlen)," ")
    if(pos<=0) exit
    equal = equal + pos
    pos = scan(attr(equal+1:attlen),"=")
    if(pos<=0) then
          call iotk_error_issue(ierrl,"iotk_scan_attr",__FILE__,__LINE__)
      goto 1
    end if
    equal = equal + pos
    if(trim(attr(equal-pos:equal-1))==trim(name)) foundl = .true.
    pos = verify(attr(equal+1:attlen)," ")
    if(pos<=0) then
          call iotk_error_issue(ierrl,"iotk_scan_attr",__FILE__,__LINE__)
      goto 1
    end if
    equal = equal + pos
    delim = attr(equal:equal)
    if(delim/="'" .and. delim/='"') then
          call iotk_error_issue(ierrl,"iotk_scan_attr",__FILE__,__LINE__)
      goto 1
    end if
    pos = scan(attr(equal+1:attlen),delim)
    if(pos<=0) then
          call iotk_error_issue(ierrl,"iotk_scan_attr",__FILE__,__LINE__)
      goto 1
    end if
    if(foundl) exit
    equal = equal + pos
  end do
  if(foundl) then
    call iotk_strcpy(valc,attr(equal+1:equal+pos-1),ierrl)
    if(ierrl/=0) then
          call iotk_error_issue(ierrl,"iotk_scan_attr",__FILE__,__LINE__)
      goto 1
    end if
  else
        call iotk_error_issue(ierrl,"iotk_scan_attr",__FILE__,__LINE__)
    ierrl = - ierrl
    goto 1
  end if
  allocate(tmpval(iotk_size(val)))
  index = 0
  call iotk_read(tmpval,valc(1:iotk_strlen(valc)),index,ierrl)
  if(ierrl/=0) then
        call iotk_error_issue(ierrl,"iotk_scan_attr",__FILE__,__LINE__)
    goto 1
  end if
  if(index/=iotk_size(val)) then
        call iotk_error_issue(ierrl,"iotk_scan_attr",__FILE__,__LINE__)
    call iotk_error_msg(ierrl,'Attribute size does not match')
    call iotk_error_write(ierrl,"attr",valc(1:iotk_strlen(valc)))
    call iotk_error_write(ierrl,"size",size(tmpval))
    goto 1
  end if
  val = reshape (source=tmpval,shape=shape(val))
  deallocate(tmpval)
1 continue
  if(present(found)) then
    found = .false.
    if(ierrl==0) found = .true.
  end if
  if(present(default)) then
    if(ierrl/=0) val = default
  end if
  if(present(ierr)) then
    ierr = ierrl
  else if((present(found) .or. present(default)) .and. ierrl<0) then
    call iotk_error_clear(ierrl)
  else
    if(ierrl/=0) call iotk_error_handler(ierrl)
  end if
end subroutine iotk_scan_attr_REAL3_1


