  use iotk_base
  use iotk_interface
  implicit none
  character(*),             intent(in)  :: attr
  character(*),             intent(in)  :: name
  REAL(kind=__IOTK_REAL3),           intent(out) :: val (:,:,:,:,:,:,:)
  logical,        optional, intent(out) :: found
  REAL(kind=__IOTK_REAL3), optional, intent(in)  :: default (:,:,:,:,:,:,:)
