    call iotk_write_attr(attr,"kind",kind(dat),ierr=ierrl)
    if(ierrl/=0) then
          call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
      goto 1
    end if
  end if
  if(usefmt/="!") call iotk_write_attr(attr,"fmt",trim(usefmt),ierr=ierrl)
  if(ierrl/=0) then
        call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
    goto 1
  end if
  call iotk_write_begin(unit,name,attr,ierrl)
  if(ierrl/=0) then
        call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
    goto 1
  end if
  if(binary) then
    if(raw) then
      write(lunit,iostat=iostat) dat
      if(iostat/=0) then
            call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
        goto 1
      end if
    else
