      if(iostat/=0) then
            call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
        goto 1
      end if
    end if
  else
    usefmt = iotk_wfmt("REAL",kind(dat),1,-1,usefmt)
    if(usefmt=="*") then
      write(lunit,*,iostat=iostat) dat
      if(iostat/=0) then
            call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
        goto 1
      end if
    else
      write(lunit,fmt=usefmt,iostat=iostat) dat
      if(iostat/=0) then
            call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
        goto 1
      end if
    end if
  end if
  call iotk_write_end(unit,name,ierr=ierrl)
  if(ierrl/=0) then
        call iotk_error_issue(ierrl,"iotk_write_dat",__FILE__,__LINE__)
    goto 1
  end if
1 continue
  if(present(ierr)) then
    ierr = ierrl
  else
    if(ierrl/=0) call iotk_error_handler(ierrl)
  end if
